import React, { useRef } from 'react'
import './css/TeacherLogin.css'
import axios from 'axios'

function TeacherLogin() {
    const email = useRef(null)
    const pass = useRef(null)
    const handlerlogin = () =>{
        if(email.current.value && pass.current.value){
            axios.post('http://localhost:8085/teacher/teacherlogin', {'email': email.current.value, 'pass': pass.current.value})
            .then((res) => {console.log(res.data)})
            .catch((err) => {console.log(err.message)})
        }else{
            console.warn("Enter credentials")
        }
        
    }

  return (
    <div>
        <div class="center">
        <h2>Teacher Login</h2>
        <form>
            <div class="txt_field">
                <input ref={email} type="text" placeholder="Email" className='username' required/>
            </div>
            <div class="txt_field">
                <input ref={pass} type="password" placeholder="Password" className="password" required/>
            </div>
            <button onClick={handlerlogin} className="submit">Login</button>
            <div name="hi"></div>
        </form>
    </div>
    </div>
  )
}

export default TeacherLogin
