import React, { useEffect, useRef } from 'react'
import { useLocation, useNavigate } from 'react-router-dom'
import jsPDF from 'jspdf'
import './css/Certificate.css'

function Certificate({result}) {
  const location = useLocation()
  const data = location.state
  const navigate = useNavigate()
  const ref = useRef()
  const handlerdownload = () =>{
      console.log(ref.current)
      // const doc = new jsPDF('p','px','a4')
      // const elementHTML = document.getElementById('content')
      // doc.addImage('http://pvppcoe.ac.in/assets/img/VISUAL%20ART%20LOGO%201.png')
      // doc.html(ref.current, {
      //   callback: function (doc) {
          
      //     doc.save("sample-document.pdf");
      //   },
      //   x: 10,
      //   y: 10,
      //   width: 800, //target width in the PDF document
      //   windowWidth: 1500, //window width in CSS pixels
      // });
    }
    useEffect(()=>{
      if(location.state == null ){
        window.alert('Enter credentials')
        navigate('/')
      }   
    },[])
  return (
    <div>
      <div>
        <button onClick={handlerdownload}>Download</button>
      </div>
      <div id='content' ref={ref}>
        <div className='innerdiv1'>
        <div className='head'>
          <img id='imgindead' src='http://pvppcoe.ac.in/assets/img/VISUAL%20ART%20LOGO%201.png' />
          <div className='inhead'>
          <h2>VASANTDADA PATIL PRATISHTHAN'S</h2>
          <h2>COLLEGE OF ENGINEERING AND VISUAL ARTS</h2>
          <h3>Vasantdada Patil Complex, Eastern Express Highway, Sion, Mumbai 400022</h3>
          <h4>Approved by AICTE, Govt. of Maharashtra, Affiliated to University of Mumbai</h4>
          </div>
        </div>
        <hr/>
        <div className='innerdiv2'>
        <h4>GRADE CARD</h4>
        <div className='innderdiv4'>
          <table>
            <tr>
              <td>NAME:</td>
              <td>{data.name.toUpperCase()}</td>
            </tr>
            <tr>
              <td>EXAMINATION:</td>
              <td>{data.exam.branch} SEM {data.exam.sem}</td>
            </tr>
            <tr>
              <td>HELD IN:</td>
              <td>{data.exam.year}</td>
            </tr>
            <tr>
              <td>SEAT NUMBER:</td>
              <td>{data.seatno}</td>
            </tr>
          </table>
        {/* <h3 id='name1'>NAME: {data.name.toUpperCase()}</h3>
        <h3 id='exam1'>EXAMINATION: {data.exam.year} {data.exam.branch} sem {data.exam.sem}</h3>
        <h3 id='seat1'>SEAT NUMBER: {data.seatno}</h3> */}
        </div>
        
      
        <ul>
          {Object.keys(data.marks).map((item, index) => {
            return <li>{item} : {Object.values(data.marks)[index]}</li>
            })}
        </ul>
        <div className='innerdiv3'>
        <h3>remark: {data.remark}</h3>
        <h2>SGPI: {data.sgpi}</h2>
        </div>
        
        </div>
        </div>
      </div>
    </div>
  )

  }
export default Certificate
